import { useEffect, useState } from'react';
import { BookPreview } from './BookPreview/BookPreview';
import { getBooks, sortByTitle } from './utils';
import { AppHeader, AppContainer, BooksContainer } from './App.style';

import './App.css';

function App() {
  const [books, setBooks] = useState([]);
  const [booksToShow, setBooksToShow] = useState([]);
  const [catalog, setCatalog] = useState([]);

    useEffect(() => {
        getBooks()
            .then(data => {
                setBooks(data);
                setBooksToShow(data);
            })
    }, []);

    useEffect(() => {
      setBooksToShow(booksToShow.sort((a, b) => sortByTitle(a,b)));
    }, [booksToShow])

    useEffect(() => {
      setCatalog(catalog.sort((a, b) => sortByTitle(a,b)));
    }, [catalog])

    function onFavoriteClick(id, isSelected) {
      if (isSelected) {
        setCatalog(catalog.filter(b => b.id!== id));
        setBooksToShow([...booksToShow, books.find(b => b.id === id)])
      } else {
        setCatalog([...catalog, books.find(b => b.id === id)]);
        setBooksToShow(booksToShow.filter(b => b.id!== id));
      }
    }

  return (
    <div className="App">
      <AppHeader>
        Book Store
      </AppHeader>
      <AppContainer>
        <BooksContainer>
          All Books:
          {booksToShow?.map((book) => (<BookPreview key={book.id} book={book} onFavoriteClick={onFavoriteClick}/>))}
        </BooksContainer>
        <BooksContainer>
          My Catalog:
          {catalog?.map((book) => (<BookPreview key={book.id} book={book} isSelected={true} onFavoriteClick={onFavoriteClick}/>))}
        </BooksContainer>
      </AppContainer>
    </div>
  );
}

export default App;
