import styled from "styled-components";

export const BookModalContainer = styled.div`
    position: fixed; /* Stay in place */
    z-index: 1; /* Sit on top */
    padding-top: 100px; /* Location of the box */
    left: 0;
    top: 0;
    width: 100%; /* Full width */
    height: 100%; /* Full height */
    overflow: auto; /* Enable scroll if needed */
    background-color: rgb(0,0,0); /* Fallback color */
    background-color: rgba(0,0,0,0.4); /* Black w/ opacity */
`;

export const BookModalContent = styled.div`
    background-color: #fefefe;
    margin: 15% auto; /* 15% from the top and centered */
    padding: 20px;
    border: 1px solid #888;
    width: 80%; /* Could be more or less, depending on screen size */
`;

export const BookTitleContainer = styled.div`
    display: flex;
    text-align: start;
    font-weight: 700;
`;

export const BookDescriptionContainer = styled.div`
    display: flex;
    text-align: start;
    font-weight: 500;
    padding-bottom: 10px;
`;

export const BookDetailsContainer = styled.div`
    display: flex;
    flex-direction: column;
    text-align: start;
    justify-content: space-between;
    max-width: 260px;
`;

export const BookHeader = styled.div`
    display: flex;
    flex-direction: column;
    text-align: start;
    gap: 6px;
`;

export const BookDetails = styled.div`
    display: flex;
    flex-direction: column;
    text-align: start;
    gap: 4px;
`;
