const BOOKS_API_URL = '/api/books';

export function getAllBooks() {
    return fetch(BOOKS_API_URL).then((res) => res.json())
}

export function getBookById(id) {
    return fetch(`${BOOKS_API_URL}/${id}`).then(res => res.json())
}
